mapper.select:
select * from t_jkmbzb where yxbz = '1'

    order by ${orderBy}

limit 1

select a.*, b.pzcsm,c.csm,c.cszff,b.pzcsmc
from t_jkmbzb a,
t_jkpzb b,
t_jkpzdmb c
where a.pzbid = b.id AND  a.cszffid = c.id
AND c.pzbid = b.id

    and   mbid = #{params.mbid}


    order by ${params.orderBy}
